import MyButton from './MyButton';
import './App.css';
import React, { effect,useState } from 'react';




function App() {

const[task,setTask]=useState("")
  const [todos,setTodos]=useState([])
  function CreateToDo(){
    setTodos(oldTodos=>{
      setTask("")

      return[...oldTodos,task]
    } )
  }



return (
  <>
  <h1>To to list App</h1>
  <input type="text"  value={task} onChange={event=>{
    setTask(event.target.value)
  }}  placeholder='Write your text ....'/>
  <button onClick={CreateToDo} > Create List</button>
  <ul>
    {todos.map(todo=>{

      return<li>{todo}</li>



    })}




  </ul>




  </>
  )
}

export default App;
